package com.decagon.eventhubbe.domain.entities;import lombok.*;import org.springframework.data.annotation.Id;import org.springframework.data.mongodb.core.mapping.DBRef;import org.springframework.data.mongodb.core.mapping.Document;@Setter@Getter@Document(collection = "account")@Builder@NoArgsConstructor@AllArgsConstructorpublic class Account {    @Id    private String id;    private String accountName;    private String accountNumber;    private String bankName;    private String bankCode;    private String subaccount_code;    @DBRef    private AppUser appUser;}