package com.decagon.eventhubbe.domain.entities;import lombok.*;import org.springframework.data.annotation.Id;import org.springframework.data.mongodb.core.mapping.Document;@Data@AllArgsConstructor@NoArgsConstructor@Document(collection = "banks")@Builder@Getter@Setterpublic class Banks {    @Id    private String id;    private String bankCode;    private String bankName;    public Banks(String bankCode,String bankName){        this.bankCode=bankCode;        this.bankName=bankName;    }}