package com.decagon.eventhubbe.domain.repository;import com.decagon.eventhubbe.domain.entities.Account;import com.decagon.eventhubbe.domain.entities.AppUser;import org.springframework.data.mongodb.repository.MongoRepository;import org.springframework.data.mongodb.repository.Query;import java.util.Optional;public interface AccountRepository extends MongoRepository<Account,String> {    Optional<Account> findByAppUser(AppUser appUser);    boolean existsByAppUser(AppUser user);    @Query("{ 'subaccount_code' : ?0}")    Optional<Account> findBySubaccount_code(String subaccount);}